def append_suffix_and_copy_files(src_directory, suffix='_ghdwpaks'):
    # src_directory가 유효한 디렉토리인지 확인
    if not os.path.isdir(src_directory):
        print(f"Error: {src_directory} is not a valid directory.")
        return

    # 디렉토리 내의 모든 파일 순회
    for filename in files:
        # 파일의 전체 경로
        src_file_path = os.path.join(src_directory, filename)

        # 파일인 경우에만 처리
        if os.path.isfile(src_file_path):
            # 새로운 파일 이름 생성
            name, ext = os.path.splitext(filename)
            new_filename = f"{name}{suffix}{ext}"
            new_file_path = os.path.join(src_directory, new_filename)

            # 파일 복사
            shutil.copy(src_file_path, new_file_path)
            print(f"Copied {src_file_path} to {new_file_path}")

# 예시로 사용할 디렉토리 경로
src_directory = 'F:\\pic\\from_cornor'

# 함수 호출
append_suffix_and_copy_files(src_directory)

print(append_suffix_and_copy_files("ghdwpaks"))
